// backend/src/image-processing/image-processing.service.ts
import { Injectable } from '@nestjs/common';
import sharp from 'sharp';

@Injectable()
export class ImageProcessingService {
  // Метод для изменения размера, сжатия и конвертации в WebP
  async processImage(buffer: Buffer, width: number, height?: number): Promise<Buffer> {
    // resize({ width, height, withoutEnlargement: true }) - избегаем увеличения, если исходник меньше
    // webp({ quality: 80 }) - сжатие в формат WebP (хорошее сжатие при сохранении качества)
    // withMetadata({ orientation: true }) - сохраняем ориентацию из EXIF, остальное удаляем (экономия размера - Принцип 9)
    return sharp(buffer)
      .resize(width, height, { withoutEnlargement: true })
      .webp({ quality: 80 })
      .withMetadata({ orientation: true })
      .toBuffer();
  }

  // Метод для простого изменения размера
  async resizeImage(buffer: Buffer, width: number, height?: number): Promise<Buffer> {
       return sharp(buffer)
           .resize(width, height, { withoutEnlargement: true })
           .toBuffer();
  }

   // Метод для конвертации в определенный формат
   async convertToFormat(buffer: Buffer, format: keyof sharp.Format): Promise<Buffer> {
       return sharp(buffer).toFormat(format).toBuffer();
   }
}
```

