// backend/src/upload/upload.controller.ts (пример обработки загрузки аватара)
import { Controller, Post, UploadedFile, UseInterceptors, Body, Inject } from '@nestjs/common';
import { FileInterceptor } from '@nestjs/platform-express';
import { ImageProcessingService } from '../image-processing/image-processing.service';
import { StorageService } from '../storage/storage.service';
import { UserService } from '../user/user.service';

@Controller('upload')
export class UploadController {
  constructor(
    private imageProcessingService: ImageProcessingService,
    private storageService: StorageService,
    private userService: UserService,
    // ... инъекция Kafka продюсера для асинхронной обработки
  ) {}

  @Post('avatar')
  @UseInterceptors(FileInterceptor('file')) // 'file' - имя поля формы
  async uploadAvatar(@UploadedFile() file: Express.Multer.File, @Body('userId') userId: string) {
    // Оптимизация: Обработка изображения перед загрузкой в R2
    const processedImageBuffer = await this.imageProcessingService.processImage(file.buffer, 300, 300); // Изменить размер и сжать
    const fileName = `avatars/${userId}-${Date.now()}.webp`; // Уникальное имя файла в папке avatars
    const mimeType = 'image/webp'; // Соответствует формату WebP

    // Загрузка обработанного файла в Cloudflare R2
    const avatarUrl = await this.storageService.uploadFile(processedImageBuffer, fileName, mimeType);

    // Обновление ссылки на аватар в профиле пользователя в Neon
    await this.userService.updateUserAvatar(userId, avatarUrl);

    // Возвращаем URL аватара клиенту
    return { avatarUrl };
