```typescript
// Плохо (N+1 проблема - отдельный запрос за каждым пользователем)
const messages = await prisma.message.findMany({ where: { chatId: chatId } });
for (const message of messages) {
  const sender = await prisma.user.findUnique({ where: { id: message.userId } });
  // ... использовать sender
}

// Хорошо (используем include для получения автора сообщения одним запросом)
const messagesWithSender = await prisma.message.findMany({
  where: { chatId: chatId },
  include: {
    user: { // Включаем данные пользователя, связанного с сообщением через поле 'user' в модели Message
      select: { id: true, name: true, avatarUrl: true } // Выбираем только нужные поля пользователя
    },
  },
  orderBy: { createdAt: 'asc' }, // Пример: сообщения в хронологическом порядке
  take: 50, // Пример: пагинация
});
```

