@Module({
  imports: [
    GraphQLModule.forRoot<ApolloDriverConfig>({
      driver: ApolloDriver,
      // ... другие настройки GraphQL (schema path, context, etc.)
      context: ({ req, connection }) => {
        // Создаем контекст для каждого запроса
        const context = { req }; // Добавляем объект запроса
         if (connection) {
           // Для WebSocket (подписки)
           // context.loaders = new Loaders(); // Если лоадеры создаются здесь
         } else {
           // Для HTTP запросов (queries/mutations)
           // Создаем лоадеры и добавляем их в контекст
           context.loaders = {
              userLoader: new UserLoader(/* зависимости */), // Нужно инжектить PrismaService
              // ... другие лоадеры
           };
           // Пример инъекции зависимостей в лоадер (может потребовать FactoryProvider)
