subsection5_1.step1_code:
import i18n from 'i18next'
import { initReactI18next } from 'react-i18next'

// Імпорт усіх файлів локалей
import en from './locales/en.json'
import es from './locales/es.json'
import fr from './locales/fr.json'
import ru from './locales/ru.json'
import ar from './locales/ar.json'

// Визначення ресурсів
const resources = {
  en: { translation: en },
  es: { translation: es },
  fr: { translation: fr },
  ru: { translation: ru },
  ar: { translation: ar },
};

i18n
  .use(initReactI18next) // Підключення react-i18next
  .init({
    resources,
    lng: 'en', // Мова за замовчуванням при першому запуску (може визначатися за системними налаштуваннями або вибором користувача)
    fallbackLng: 'en', // Резервна мова, якщо поточний переклад відсутній
    interpolation: {
      escapeValue: false // Дозволяє використовувати HTML теги в перекладах (обережно!)
    }
  });

export default i18n;

subsection5_1.step2_code:
import { useTranslation } from 'react-i18next';
import { Text, Button, View } from 'react-native';

const WelcomeScreen = () => {
  const { t, i18n } = useTranslation(); // Отримуємо функцію t та об'єкт i18n

  return (
    <View>
      {/* Використання простого перекладу */}
      <Text>{t('welcomeTitle')}</Text>

      {/* Використання перекладу з інтерполяцією */}
      {/* Передбачається, що десь є стан unreadCount */}
      <Text>{t('chat.unreadCount', { count: 5 })}</Text> 

      {/* Використання перекладу для тексту кнопки */}
      <Button title={t('sendButton')} onPress={sendMessage} />

      {/* Приклад зміни мови */}
      <Button title={t('settings.language')} onPress={() => i18n.changeLanguage('ar')} />
    </View>
  );
};

