subsection5_3.react_native_code:
import { I18nManager } from 'react-native';
import i18n from './index'; // Ваш файл ініціалізації i18next

// У головному файлі додатка, перед рендером
const isRTL = i18n.language === 'ar' || i18n.language === 'he'; // Перевіряємо, чи є поточна мова RTL

// forceRTL може вимагати перезавантаження додатка для повної сили
if (isRTL !== I18nManager.isRTL) {
  I18nManager.forceRTL(isRTL);
  // Optional: Restart the app to apply RTL layout fully
  // RNRestart.Restart(); 
}

// Деякі стилі можуть вимагати ручної адаптації, якщо auto-conversion недостатньо
const containerStyle = {
  flexDirection: isRTL ? 'row-reverse' : 'row',
  textAlign: isRTL ? 'right' : 'left',
};

section9.dates_code:
import { FormattedDate, FormattedTime } from 'react-intl';

// Відобразить дату у форматі, що відповідає поточній локалі
<FormattedDate value={new Date()} /> // Наприклад, "3/14/2025" (en-US) або "14.3.2025" (de-DE)
<FormattedDate value={new Date()} weekday="long" year="numeric" month="long" day="numeric" /> // "Friday, March 14, 2025"

// Відобразить час
<FormattedTime value={new Date()} /> // Наприклад, "3:00:00 PM" (en-US) або "15:00:00" (de-DE)

section9.numbers_code:
import { FormattedNumber, FormattedCurrency } from 'react-intl';

// Відобразить число з локальним роздільником тисяч та десятковим знаком
<FormattedNumber value={1234.56} /> // "1,234.56" (en-US) або "1.234,56" (de-DE)

// Відобразить число як відсоток
<FormattedNumber value={0.75} style="percent" /> // "75%"

// Відобразить число як валюту (вимагає коду валюти)
<FormattedCurrency value={123.45} currency="USD" /> // "$123.45" (en-US)
